using System;
using System.Net.Http;
using System.Net.Http.Headers;
using System.Text;
using System.Threading.Tasks;

namespace ProductionDataSvc.AcceptanceTests.Utils
{
  public static class RestClient
  {
    public static async Task<HttpResponseMessage> SendHttpClientRequest(string baseAddress, string route, HttpMethod method, string acceptHeader, string contentType, string payloadData)
    {
      return await SendHttpClientRequest(new Uri($"{baseAddress}{route}"), method, acceptHeader, contentType, payloadData);
    }

    public static async Task<HttpResponseMessage> SendHttpClientRequest(Uri uri, HttpMethod method, string acceptHeader, string contentType, string payloadData)
    {
      Console.WriteLine($"resourceURL ({method.Method}): {uri.AbsoluteUri}");

      var client = new HttpClient { BaseAddress = uri };
      client.DefaultRequestHeaders.Accept.Add(new MediaTypeWithQualityHeaderValue(acceptHeader));
      client.DefaultRequestHeaders.Add("X-VisionLink-CustomerUid", "87bdf851-44c5-e311-aa77-00505688274d");
      client.DefaultRequestHeaders.Add("X-JWT-Assertion", "eyJ0eXAiOiJKV1QiLCJhbGciOiJTSEEyNTZ3aXRoUlNBIiwieDV0IjoiWW1FM016UTRNVFk0TkRVMlpEWm1PRGRtTlRSbU4yWmxZVGt3TVdFelltTmpNVGt6TURFelpnPT0ifQ==..kTaMf1IY83fPHqUHTtVHn6m6aQ9wFch6c0FsNDQ7x1k=");
      client.DefaultRequestHeaders.Add("X-VisionLink-ClearCache", "true");
      client.DefaultRequestHeaders.Add("pragma", "no-cache");

      var requestMessage = new HttpRequestMessage(method, uri);

      if (payloadData != null)
      {
        requestMessage.Content = new StringContent(payloadData, Encoding.UTF8, contentType);
      }

      var result = await client.SendAsync(requestMessage);

      return result;
    }

    public static readonly string Productivity3DServiceBaseUrl = ConstructUri(Environment.GetEnvironmentVariable("COMPACTION_SVC_BASE_URI"));

    private static string ConstructUri(string subDir)
    {
      return $"http://{Environment.GetEnvironmentVariable("RAPTOR_WEBSERVICES_HOST")}{subDir}";
    }
  }
}
