using System;
using System.Net.Http;
using System.Net.Http.Headers;
using System.Text;
using System.Threading.Tasks;

namespace ProductionDataSvc.AcceptanceTests.Utils
{
  public class RestClient
  {
    public static readonly string Productivity3DServiceBaseUrl;

    private static readonly HttpClient httpClient;

    static RestClient()
    {
      httpClient = new HttpClient();

      httpClient.DefaultRequestHeaders.Add("X-VisionLink-CustomerUid", "87bdf851-44c5-e311-aa77-00505688274d");
      httpClient.DefaultRequestHeaders.Add("X-JWT-Assertion", "eyJ0eXAiOiJKV1QiLCJhbGciOiJTSEEyNTZ3aXRoUlNBIiwieDV0IjoiWW1FM016UTRNVFk0TkRVMlpEWm1PRGRtTlRSbU4yWmxZVGt3TVdFelltTmpNVGt6TURFelpnPT0ifQ==..kTaMf1IY83fPHqUHTtVHn6m6aQ9wFch6c0FsNDQ7x1k=");
      httpClient.DefaultRequestHeaders.Add("X-VisionLink-ClearCache", "true");
      httpClient.DefaultRequestHeaders.Add("pragma", "no-cache");

      Productivity3DServiceBaseUrl = $"http://{Environment.GetEnvironmentVariable("RAPTOR_WEBSERVICES_HOST")}:{Environment.GetEnvironmentVariable("COMPACTION_SVC_BASE_URI").TrimStart(':')}";
    }

    public static Task<HttpResponseMessage> SendHttpClientRequest(string baseAddress, string route, HttpMethod method, string acceptHeader, string contentType, string payloadData = null)
    {
      var requestMessage = new HttpRequestMessage(method, new Uri($"{baseAddress}{route}"));

      if (payloadData != null)
      {
        switch (contentType)
        {
          case MediaTypes.JSON:
            {
              requestMessage.Content = new StringContent(payloadData, Encoding.UTF8, contentType);

              break;
            }
          case MediaTypes.MULTIPART_FORM_DATA:
            {
              throw new NotImplementedException();
            }
        }
      }

      requestMessage.Headers.Accept.Add(new MediaTypeWithQualityHeaderValue(acceptHeader));

      Console.WriteLine($"[{method}] {requestMessage.RequestUri.AbsoluteUri}");

      return httpClient.SendAsync(requestMessage);
    }
  }
}
